@import url('https://fonts.googleapis.com/css2?family=Open+Sans:ital,wght@0,300;0,400;0,500;0,600;0,700;0,800;1,300;1,400;1,500;1,600;1,700;1,800&display=swap');

/* Reset some default browser styles */
body,
h1,
h2,
p {
    margin: 0;
    padding: 0;
}

/* Set a default font and color */
body {
    // font-family: Arial, sans-serif;
    color: #231F20;
    font-family: 'Open Sans', sans-serif;
    font-size: 14px;
}

/* Style heading elements */
h1 {
    font-size: 24px;
}

h2 {
    font-size: 20px;
}

/* Style paragraphs */
p {
    font-size: 16px;
    line-height: 1.5;
}

/* Add some basic styles to links */
a {
    text-decoration: none;
    color: #007bff;

    &:hover {
        text-decoration: none !important;
    }
}

a:hover {
    text-decoration: underline;
}

.container-fluid {
    padding-left: 30px;
    padding-right: 30px;
}

@media (max-width: 1540px) {
    .container-fluid {
        max-width: 1540px;
    }
}

@media (max-width: 767px) {
    .container-fluid {
        padding-left: 15px;
        padding-right: 15px;
    }
}

img {
    max-width: 100%;
}

button:focus {
    box-shadow: unset !important;
    outline: unset;
}

button {
    background: transparent;
    border: none;
}

.wrapper {
    max-width: 1920px;
    margin: auto;
    overflow: hidden;
}

.cover-banner {
    img {
        width: 100%;
    }
}

.title-heading {
    font-size: 1.6em;
    font-weight: 700;
    color: #231F20;
    margin-bottom: 0;
    position: relative;
    padding-left: 60px;
    text-transform: uppercase;

    &::before {
        content: "";
        background-image: url('/assets/images/title-heading.png');
        min-width: 38px;
        min-height: 8px;
        position: absolute;
        left: 0;
        top: 10px;
    }

    @media (max-width: 767px) {
        font-size: 1.2rem;
    }
}

.header {
    min-height: 70px;
    position: fixed;
    width: 100%;
    top: 0;
    left: 0;
    right: 0;
    background: transparent;
    padding: 1rem 0;
    z-index: 99;

    &.default {
        background: #fff;
        position: relative;
        border-bottom: 1px solid #d9d9d9;

        img {
            filter: brightness(0%);
        }

        .header-main--menu .btn-menu {
            color: #000;
        }
    }

    &-main {
        display: flex;
        align-items: center;
        justify-content: space-between;

        &--menu {
            display: inline-flex;
            align-items: center;
            gap: 25px;

            .btn-menu {
                background: transparent;
                border: none;
                font-size: 26px;
                color: #fff;

                svg {
                    width: 25px;
                }
            }
        }

        &--controler {
            display: inline-flex;
            align-items: center;
            gap: 10px;

            .btn {
                border: none;
                background: transparent;
                text-align: center;
            }
        }

        @media (max-width: 767px) {
            &--controler {
                gap: 0;
            }
        }
    }

    &.scrolled {
        padding: 1rem 0;
        background: #fff;
        position: fixed;
        width: 100%;
        top: 0;
        left: 0;
        right: 0;

        img {
            filter: brightness(0%);
        }

        .header-main--menu .btn-menu {
            color: #000;
        }
    }

    &.open-search {
        background: #fff;

        img {
            filter: brightness(0%);
        }

        .header-main--menu .btn-menu {
            color: #000;
        }
    }

    @media (max-width: 767px) {
        padding: 1rem 0;

        &.open-search {
            .header-main--controler {

                .btn-2,
                .btn-3,
                .btn-4 {
                    display: none;
                }
            }
        }
    }
}

.menu-group {
    background: #fff;
    min-width: 430px;
    height: 100%;
    top: 0;
    position: fixed;
    bottom: 0;
    z-index: 99;
    padding: 30px;
    padding-bottom: 75px;
    transition: .3s;
    left: -1000px;

    &.open {
        left: 0;
    }

    &::after {
        content: "";
        background-image: url('/assets/images/bgr-menu.png');
        min-width: 100%;
        position: absolute;
        right: 0;
        height: 800px;
        background-size: contain;
        top: 0%;
        opacity: 1;
        z-index: -1;
        background-repeat: no-repeat;
        background-position: right;
    }

    .btn-close-menu {
        border: none;
        background: transparent;
        padding: 0;

        svg {
            width: 20px;
        }
    }

    .menu-group--list {
        list-style: none;
        padding: 0;
        margin: 20px 0;
        position: relative;

        li {
            margin-bottom: 30px;

            a {
                color: #000;
                text-transform: uppercase;
            }
        }
    }

    .menu-bottom {
        position: absolute;
        bottom: 0;
        min-height: 60px;
        width: 100%;
        left: 0;
        padding: 30px;
        border-top: 1px solid #EAEAEA;
        display: flex;
        align-items: center;
        justify-content: space-between;

        a {
            display: inline-block;
            color: #231F20;
            text-decoration: underline;
        }

        &::before {
            content: "";
            background: #fff;
            background-image: url('/assets/images/icon-line-menu.png');
            width: 60px;
            height: 20px;
            position: absolute;
            top: -9px;
            background-repeat: no-repeat;
            background-position: center;
            margin: auto;
            left: 0;
            right: 0;
        }
    }

    @media (max-width: 767px) {
        max-width: 100%;
        width: 100%;
        min-width: unset;
    }
}

.image-style-list {
    position: relative;

    .list-container {
        overflow: hidden;
        position: relative;
        max-width: 100%;
    }

    .list-style-group,
    .list-style-group-ctgr {
        display: flex;
        transition: transform 0.3s ease-in-out;
    }

    &--item {
        flex: 0 0 33.33%;
        /* Hiển thị 3 mục trên màn hình desktop */
        box-sizing: border-box;
        text-align: center;

        // padding: 20px;
        img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            object-position: top;
        }
    }

    .btn-control-slide {
        position: absolute;
        top: 50%;
        border-radius: 100%;
        border: none;
        background: #ffffff59;
        width: 40px;
        height: 40px;
        margin: 0 15px;
        opacity: 1;
        display: none;

        &.next-button,
        &.next-button-ctgr {
            right: 0;
        }

        &.prev-button,
        &.prev-button-ctgr {
            left: 0;
            transform: rotate(180deg);
        }

        &[disabled] {
            opacity: 0;
        }
    }

    @media (max-width: 767px) {
        .btn-control-slide {
            display: block;
        }

        &--item {
            flex: 0 0 100%;
        }
    }
}

.list-category {
    margin: 30px 0 0 30px;

    .btn-control-slide {
        display: block;
    }


    .image-style-list--item {
        flex: 0 0 25%;
    }

    .list-style-group-ctgr {
        gap: 15px;
    }

    .item-category {
        position: relative;
        max-height: 480px;
        overflow: hidden;

        &--info {
            position: absolute;
            bottom: 0;
            width: 100%;
            left: 0;
            right: 0;
            text-align: left;
            padding: 15px;
            margin-bottom: 30px;

            h3 {
                font-size: 18px;
                color: #fff;
                font-weight: 600;
                margin-bottom: 0;
            }

            a {
                font-size: 14px;
                color: #fff;
                position: relative;

                &::after {
                    content: "";
                    position: absolute;
                    width: 100%;
                    height: 2px;
                    background: #fff;
                    left: 0;
                    bottom: -7px;
                }
            }
        }
    }

    @media (min-width: 1500px) {
        .item-category {
            max-height: 630px;
        }
    }

    @media (max-width: 991px) {
        margin: 0;

        .list-style-group-ctgr {
            gap: 0;
        }

        .item-category {
            &--info {
                h3 {
                    font-size: 15px;
                    margin-bottom: 5px;
                }

            }
        }

    }


    @media (max-width: 767px) {
        .image-style-list--item {
            flex: 0 0 100%;
        }

        .item-category {
            max-height: 480px;
        }
    }

}

.group-production {
    margin: 30px 0;

    &--heading {
        display: flex;
        align-items: center;
        justify-content: space-between;
        margin-bottom: 30px;

        .title-heading {
            font-size: 1.6em;
            font-weight: 700;
            color: #231F20;
            margin-bottom: 0;
            position: relative;
            padding-left: 60px;
            text-transform: uppercase;

            &::before {
                content: "";
                background-image: url('/assets/images/title-heading.png');
                min-width: 38px;
                min-height: 8px;
                position: absolute;
                left: 0;
                top: 10px;
            }
        }
    }

    &--footer {
        border: none;
    }

    .view-prd-mobile {
        display: none;
    }

    // .view-more-prd{
    //     display: inline-flex;
    //     align-items: center;
    //     justify-content: center;
    //     border-radius: 40px;
    //     border: 1px solid #000;
    //     color: #000;
    //     padding: 8px;
    //     min-width: 150px;
    //     font-weight: 600;
    //     position: relative;

    //     &::after{
    //         content: "";
    //         border: 2px solid #BCBEC0;
    //         padding: 21px;
    //         position: absolute;
    //         min-width: 158px;
    //         min-height: 47px;
    //         border-radius: 40px;
    //         left: -5px;
    //         right: 0;
    //         top: -5px;
    //         margin: auto;
    //     }

    //     &:hover{
    //         background: #231F20;
    //         color: #fff;
    //     }

    //     &
    // }

    &--list {
        display: flex;
        gap: 15px;
        flex-wrap: wrap;
        list-style: none;
        padding: 0;
        margin: 0;

        &-item {
            width: calc(100%/4 - 12px);

            .thumbnail-product {
                position: relative;
                overflow: hidden;

                img {
                    width: 100%;
                }

                .label-sale {
                    position: absolute;
                    top: 20px;
                    left: 20px;
                    display: inline-block;
                    text-align: center;
                    font-weight: 600;
                    padding: 3px 8px;
                    background: #fff;
                    border: 1px solid #000;
                    font-size: 12px;
                    border-radius: 40px;

                    &--percent {
                        right: 20px;
                        left: unset;
                        background: #000;
                        color: #fff;
                        font-size: 11px;
                        font-weight: 500;
                    }
                }
            }


            .select-color-prd {
                position: absolute;
                left: 0;
                right: 0;
                // bottom: 15px;
                z-index: 1;
                margin: auto;
                text-align: center;
                bottom: -1000px;
                transition: .5s;

                &--group {
                    display: inline-flex;
                    align-items: center;
                    justify-content: center;
                    background: #fff;
                    border-radius: 40px;
                    padding: 10px;
                    gap: 10px;

                    .color-item {
                        width: 15px;
                        height: 15px;
                        display: inline-flex;
                        border-radius: 100%;
                        background: #ebebeb;
                        cursor: pointer;
                        position: relative;

                        &.active {
                            &::after {
                                content: "";
                                border: 1px solid #9f9f9f;
                                width: 21px;
                                height: 21px;
                                border-radius: 100%;
                                overflow: hidden;
                                position: absolute;
                                left: -3px;
                                right: 0;
                                top: -3px;
                                bottom: 0px;
                            }
                        }
                    }
                }
            }

            .btn-add-cart {
                display: inline-flex;
                align-items: center;
                justify-content: center;
                background: #fff;
                border-radius: 100%;
                width: 36px;
                height: 36px;
                padding: 5px;
                position: absolute;
                bottom: 15px;
                right: 15px;
                cursor: pointer;
                z-index: 2;
                opacity: 0;
                visibility: hidden;
                transition: .5s;

                img {
                    filter: brightness(0%);
                    width: 14px;
                }
            }

            .select-size-prd {
                position: absolute;
                z-index: 1;
                bottom: 70px;
                width: 90%;
                margin: auto;
                left: 0;
                right: 0;
                padding: 15px;
                background: #ffffffc2;
                border-radius: 15px;
                text-align: center;
                display: none;

                p {
                    font-size: 13px;
                    margin-bottom: 10px;
                }

                &--group {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    gap: 5px;

                    &---item {
                        display: flex;
                        width: 30px;
                        height: 30px;
                        flex-direction: column;
                        justify-content: center;
                        align-items: center;
                        border-radius: 100%;
                        border: 1px solid #EAEAEA;
                        background: #fff;
                        font-size: 12px;
                        font-weight: 600;
                        cursor: pointer;

                        &.active {
                            border-color: #757575;
                        }

                        &.out-of {
                            color: #d1d1d1;
                            cursor: no-drop;
                            user-select: none;
                            position: relative;
                            overflow: hidden;

                            &::after {
                                content: "";
                                height: 100%;
                                width: 1px;
                                background: #e3e3e3;
                                top: 0;
                                bottom: 0;
                                position: absolute;
                                transform: rotate(45deg);
                            }
                        }
                    }
                }
            }


            .info-product {
                padding: 15px 0;

                &--heading {
                    display: flex;
                    justify-content: space-between;

                    .title-product {
                        font-size: 16px;
                        font-weight: 400;

                        a {
                            color: #000;
                        }
                    }

                    .favorite-prd {
                        cursor: pointer;
                        margin-left: 10px;
                        display: inline-block;

                        svg {
                            width: 18px;
                        }
                    }
                }

                &--price {
                    display: flex;
                    align-items: center;
                    gap: 15px;

                    .price-main {
                        font-weight: 400;
                        font-size: 15px;
                    }

                    .price-sale {
                        color: #696969;
                        text-decoration: line-through;
                        font-size: 14px;
                    }
                }
            }

            &:hover {
                .thumbnail-product {
                    .select-color-prd {
                        bottom: 15px;
                        transition: .5s;
                    }
                }

                .btn-add-cart {
                    opacity: 1;
                    visibility: visible;
                    transition: .5s;
                }

                .select-size-prd {
                    &.show {
                        display: block;
                    }
                }
            }
        }
    }

    @media (max-width: 991px) {
        &--list {
            display: flex;
            overflow-x: auto;
            scroll-snap-type: x;
            flex-wrap: nowrap;

            &-item {
                scroll-snap-align: start;
                min-width: 230px;

                .info-product {
                    .title-product {
                        font-size: 14px;
                    }

                }
            }
        }
    }

    @media (max-width: 767px) {
        &--footer {
            display: block;
        }

        &--heading {
            .btn-custom {
                display: none;
            }
        }

        .title-heading {
            font-size: 16px;

            &::before {
                min-width: 38px;
                min-height: 8px;
                position: absolute;
                left: 0;
                top: 5px;
            }
        }

        .view-more-prd {
            display: none;

            &.view-prd-mobile {
                // display: block;
                // text-align: center;
                // margin: 20px;

                // &::after{
                //     min-width: 313px;
                // }
            }
        }

        .view-prd-mobile {
            width: 100%;
            margin-top: 20px;
            display: inline-flex;
        }
    }
}

.footer {
    background: #231F20;
    position: relative;
    min-height: 400px;
    padding: 80px 0 40px;

    .container-fluid {
        position: relative;
        z-index: 10;
    }

    &::after {
        content: "";
        background-image: url('/assets/images/line-footer.png');
        position: absolute;
        bottom: 0;
        left: 0;
        width: 100%;
        height: 25px;
    }

    &::before {
        content: "";
        background-image: url('/assets/images/icon-footer-background.png');
        width: 900px;
        height: 330px;
        position: absolute;
        background-size: cover;
        margin: auto;
        background-repeat: no-repeat;
        left: 0;
        right: 0;
        bottom: 0;
        text-align: center;
    }

    .heading-footer {
        text-align: center;
        font-size: 26px;
        text-transform: uppercase;
        color: #fff;
        font-weight: 300;
    }

    .form-contact {
        display: flex;
        align-items: center;
        gap: 20px;
        justify-content: center;
        margin: 20px 0;

        .form-control {
            border-radius: 24px;
            background: #fff;
            min-height: 48px;
            max-width: 240px;
            font-size: 14px;
        }

        .btn-submit {
            background: transparent;
            border: 1px solid #fff;
            color: #fff;
            border-radius: 40px;
            min-width: 135px;
            min-height: 40px;
            position: relative;
            font-size: 14px;

            &::after {
                content: "";
                border: 2px solid #BCBEC0;
                padding: 21px;
                position: absolute;
                min-width: 143px;
                min-height: 48px;
                border-radius: 40px;
                left: -5px;
                right: 0;
                top: -5px;
                margin: auto;
            }

            &:hover {
                background: #fff;
                color: #000;
            }
        }
    }

    &-menu-group {
        border-top: 1px solid #696969;
        border-bottom: 1px solid #696969;
        margin: 30px 0;
        position: relative;

        &::before {
            content: "";
            background: #272324;
            background-image: url('/assets/images/icon-small-footer.svg');
            width: 60px;
            height: 20px;
            position: absolute;
            bottom: -9px;
            background-repeat: no-repeat;
            background-position: center;
            margin: auto;
            left: 0;
            right: 0;
        }
    }

    &-menu-list {
        display: flex;
        align-items: center;
        list-style: none;
        justify-content: space-evenly;
        position: relative;
        padding: 30px 0;
        padding-bottom: 40px;

        &--item {
            a {
                color: #fff;
                text-align: center;
                display: inline-block;
                position: relative;

                &::after {
                    content: "";
                    width: 100%;
                    height: 1px;
                    background: #fff;
                    position: absolute;
                    bottom: -10px;
                    left: 0;
                }
            }
        }
    }

    &-coppy-right {
        display: flex;
        justify-content: space-between;
        align-items: center;
        color: #fff;
        margin-top: 50px;
        margin-bottom: 50px;
    }

    @media (max-width: 767px) {
        padding: 40px 0 10px;

        &::before {
            background-size: contain;
            background-position: bottom;
            width: 100%;
            height: 100%;
        }

        .heading-footer {
            font-size: 20px;
        }

        &-menu-list {
            display: flex;
            overflow-x: auto;
            scroll-snap-type: x;
            flex-wrap: nowrap;

            &--item {
                scroll-snap-align: start;
                min-width: 100px;
                text-align: center;
            }
        }
    }
}

.form-with-icon {
    position: relative;

    .form-control {
        padding-right: 45px;
    }

    .ic-form-control {
        width: 20px;
        position: absolute;
        right: 10px;
        top: 15px;
    }
}

.cover-banner {

    /* Thiết lập slider và các slide */
    .slider-container {
        position: relative;
        overflow: hidden;
        width: 100%;
    }

    .slider {
        display: flex;
        transition: transform 0.3s ease;
        /* Thời gian và kiểu chuyển động */
    }

    .slide {
        flex: 0 0 100%;
        width: 100%;
        text-align: center;
    }

    /* Thiết lập dots */
    .dots-container {
        display: flex;
        justify-content: center;
        position: absolute;
        bottom: 20px;
        left: 0;
        right: 0;
        margin: auto;
    }

    .dot {
        width: 5px;
        height: 5px;
        background-color: #fff;
        border-radius: 40px;
        margin: 0 5px;
        cursor: pointer;
    }

    .dot.active {
        background-color: #fff;
        /* Màu của dot hiện tại */
        width: 60px;
    }

    @media (max-width: 767px) {
        img {
            width: 100%;
            min-height: 390px;
            -o-object-fit: cover;
            object-fit: cover;
            aspect-ratio: 16/9;
            -o-object-position: left;
            object-position: center;

            &.img-mb {
                min-height: 100vh;
            }
        }
    }
}

.search-box {
    width: 100%;
    min-height: 300px;
    background: #fff;
    position: fixed;
    top: 73px;
    z-index: 99;
    border-top: 1px solid #e1e1e1;
    padding: 20px 30px;
    display: none;

    &.open {
        display: block;
    }

    &--header {
        padding-bottom: 20px;
        border-bottom: 1px solid #e1e1e1;
        margin-bottom: 20px;
    }

    .group-production {
        margin: 0;
    }

    .search-tag-group {
        display: flex;
        align-items: center;
        flex-wrap: wrap;
        gap: 10px;

        &--item {
            border: 1px solid #EAEAEA;
            border-radius: 40px;
            color: #231F20;
            display: inline-flex;
            padding: 5px 10px;
            font-size: 13px;
            font-weight: 500;
            cursor: pointer;

            &:hover {
                background: whitesmoke;
            }
        }
    }

    .group-production--list-item {
        width: calc(100%/6 - 13px);
    }

    .group-production--list-item .info-product--heading .title-product,
    .group-production--list-item .info-product--price .price-main {
        font-size: 14px;
    }

    .group-production--heading .title-heading {
        font-size: 18px;
    }

    .group-production--heading .title-heading:before {
        min-height: 8px;
        top: 7px;
    }

    .group-production--list {
        display: flex;
