.list-product-category {
    position: relative;

    .group-production--list {
        display: grid;
        grid-template-columns: repeat(4, 1fr);
        grid-auto-rows: calc(23%);
        /* Chiều cao của mỗi hàng */
        max-width: 100%;
        /* Độ rộng tối đa của danh sách */
        margin: 0 auto;
        /* Căn giữa danh sách */
        grid-gap: 15px;
        /* Khoảng cách giữa các item */
        grid-auto-flow: dense;
        /* Sử dụng grid-auto-flow với giá trị dense */

        &-item {
            width: 100%;
            // overflow: hidden;

            .info-product--heading {
                .title-product {
                    font-size: 15px;
                }
            }

            &:nth-child(1) {
                width: 100%;
                grid-column: auto / span 2;
                /* Item lớn chiếm cả hai cột */
                grid-row: auto / span 2;
                /* Item lớn chiếm 2 hàng */
                height: 100%;
                // max-height: 1350px;
                position: relative;

                .info-product {
                    visibility: hidden;
                    opacity: 0;
                    position: absolute;
                    width: 100%;
                    max-width: 320px;
                    bottom: 30%;
                    z-index: 1;
                    left: 60px;
                    background: #ffffffd9;
                    padding: 20px;
                    border-radius: 20px;
                    transition: .3s all;

                    .select-color-prd {
                        position: unset;
                        text-align: left;

                        &--group {
                            background: transparent;
                            padding: 0;
                            margin: 13px 0 0;
                        }
                    }

                    .btn-add-cart {
                        display: block;
                        background: transparent;
                        opacity: 1;
                        visibility: visible;
                    }
                }

                .thumbnail-product {
                    height: 100%;

                    img {
                        height: 100%;
                        aspect-ratio: 9/16;
                        object-fit: cover;
                    }
                }
            }

            &:nth-child(2) {
                grid-column: 3;
                grid-row: 1;
            }

            &:nth-child(3) {
                grid-column: 4;
                grid-row: 1;
            }

            &:nth-child(4) {
                grid-column: 3;
                grid-row: 2;
            }

            &:nth-child(5) {
                grid-column: 4;
                grid-row: 2;
            }

            &:hover {
                &:nth-child(1) {
                    .info-product {
                        visibility: visible;
                        opacity: 1;
                    }
                }
            }
        }

        &-default {
            grid-auto-rows: unset !important;

            .group-production--list-item {

                &:nth-child(1),
                &:nth-child(2),
                &:nth-child(3),
                &:nth-child(4),
                &:nth-child(5) {
                    grid-column: unset;
                    grid-row: unset;
                }

                &:nth-child(1) {
                    .info-product {
                        visibility: visible;
                        opacity: 1;
                        position: unset;
                        width: 100%;
                        max-width: unset;
                        // bottom: 30%;
                        // z-index: 1;
                        // left: 60px;
                        padding: 15px 0;
                        background: #fff;
                        border-radius: 0;
                        transition: unset;

                        .select-color-prd {
                            position: unset;
                            text-align: left;

                            &--group {
                                background: transparent;
                                padding: 0;
                                margin: 13px 0 0;
                            }
                        }

                        .btn-add-cart {
                            display: block;
                            background: transparent;
                            opacity: 1;
                            visibility: visible;
                        }
                    }

                    .thumbnail-product {
                        height: auto;
                        margin-bottom: 0;

                        img {
                            height: auto;
                            aspect-ratio: unset;
                            -o-object-fit: unset;
                            object-fit: unset;
                        }
                    }
                }
            }
        }

        @media (max-width: 991px) {
            grid-template-columns: repeat(3, 1fr);
            grid-auto-rows: calc(16%);

            &-item {
                min-width: unset;

                &:nth-child(3) {
                    grid-column: 3;
                    grid-row: 2;
                }

                &:nth-child(4),
                &:nth-child(5) {
                    grid-column: unset;
                    grid-row: unset;
                }
            }
        }

        @media (max-width: 767px) {
            // grid-template-columns: repeat(2, 1fr);
            // grid-auto-rows: calc(12%);
            overflow-x: unset;
            scroll-snap-type: unset;

            display: flex;
            flex-wrap: wrap;
            align-items: center;
            gap: 15px;

            &-item {
                min-width: unset;
                width: calc(50% - 8px);

                &:nth-child(2),
                &:nth-child(3),
                &:nth-child(4),
                &:nth-child(5) {
                    grid-column: unset;
                    grid-row: unset;
                }

                .info-product--heading {
                    .title-product {
                        font-size: 14px;
                    }
                }

                &:first-child {
                    .thumbnail-product {
                        height: 100%;
                        max-height: 480px;
                        margin-bottom: 15px;

                        img {
                            aspect-ratio: unset;
                            object-fit: contain;
                            object-position: top;
                        }
                    }

                    .info-product {
                        visibility: visible;
                        opacity: 1;
                        position: unset;
                        background: transparent;
                        padding: 0;
                        position: relative;
                        max-width: 100%;
                        left: 0;
                    }
                }

            }

            &-default {
                .group-production--list-item {

                    &:nth-child(1),
                    &:nth-child(2),
                    &:nth-child(3),
                    &:nth-child(4),
                    &:nth-child(5) {
                        grid-column: unset;
                        grid-row: unset;
                        width: calc(50% - 8px);
                    }
                }
            }
        }

    }

    .paginate-menu {
        position: absolute;
        bottom: 0;
        left: 0;
        right: 0;
        margin-bottom: 20px;
    }

    &.list-product-resuilt {
        .paginate-menu {
            position: unset;
        }
    }

    @media (max-width: 767px) {
        .paginate-menu {
            position: unset;
            margin: 30px 0;

            &--list {
                margin-bottom: 0;
            }
        }
    }
}

.paginate-menu {
    text-align: center;

    &--list {
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 30px;
        list-style: none;
        padding: 0;
        margin: 0 0 20px;

        a {
            color: #231F20;
            font-size: 14px;
        }

        &-item {
            a {
