            color: #2cac87;
            background: #ddf1e1;
        }

        .wizard.numeric li:not(:last-child).active span {
            color: #fff;
            background: #479A5F;
            // border: 3px solid #2cac87;
        }

        .wizard.numeric li:not(:last-child):after {
            content: "";
            background: #d6d6d6;
            height: 4px;
            border-radius: 4px;
            display: flex;
            margin: -4em 0em 0em 13.7em;
            width: 100%;
            position: absolute;
            z-index: -1;
        }

        .wizard.numeric li:not(:last-child).done {
            &:after {
                background: #479A5F;
            }
        }

        .wizard-body {
            font-size: 13px;
            text-align: center;
            margin-top: 15px;

            h5 {
