    .btn-zalo, .btn-mess{
        width: 50px;
        height: 50px;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.10);
    }

    .list-widget{
        display: inline-flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        gap: 15px;
        position: absolute;
        top: -215px;
        right: 0;
        padding: 20px 0;
        visibility: hidden;
        opacity: 0;
        transition: all .5s;
    }

    // .btn-hover-widget{
    //     &:hover{
    //         &+.list-widget{
    //             visibility: visible;
    //             opacity: 1;
    //             transition: all .5s;
    //         }
    //     }
    // }

    &:hover{
        .list-widget{
            visibility: visible;
            opacity: 1;
            transition: all .5s;
        }
    }
}
