+/* Modal Styles */
+.modal-custom {
+    display: none;
+    position: fixed;
+    z-index: 1000;
+    left: 50%;
+    top: 50%;
+    transform: translate(-50%, -50%);
+    background-color: white;
+    padding: 20px;
+    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
+    width: 100%;
+    max-width: 620px;
+    min-height: 693px;
+
+    .modal-custom-content {
+        padding-top: 1rem;
+    }
+    
+    .close {
+        position: absolute;
+        top: 15px;
+        right: 15px;    
+        cursor: pointer;
+
+        svg{
+            width: 20px;
+        }
+    }
+
+    @media (max-width: 767px) {
+        width: 90%;
+    }
+}
+
+ /* Backdrop Styles */
+ .modal-backdrop {
+    display: none;
+    position: fixed;
