+
+    &::before{
+        content: "";
+        background-image: url('/assets/images/title-heading.png');
+        min-width: 38px;
+        min-height: 8px;
+        position: absolute;
+        left: 0;
+        top: 10px;
+    }
+
+    @media (max-width: 767px) {
+        font-size: 1.2rem;
+    }
+}
