+.header{
+    min-height: 70px;
+    position: fixed;
+    width: 100%;
+    top: 0;
+    left: 0;
+    right: 0;
+    background: transparent;
+    padding: 1rem 0;
+    z-index: 99;
+
+    &.default{
+        background: #fff;
+        position: relative;
+        border-bottom: 1px solid #d9d9d9;
+
+        img{
+            filter: brightness(0%);
