+        position: relative;
+        max-width: 100%;
+    }
+
+    .list-style-group, .list-style-group-ctgr {
+        display: flex;
+        transition: transform 0.3s ease-in-out;
+    }
+
+    &--item {
+        flex: 0 0 33.33%; /* Hiển thị 3 mục trên màn hình desktop */
+        box-sizing: border-box;
+        text-align: center;
+        // padding: 20px;
+        img{
+            width: 100%;
+            height: 100%;
+            object-fit: cover;
+            object-position: top;
