+    .btn-control-slide{
+        position: absolute;
+        top: 50%;
+        border-radius: 100%;
+        border: none;
+        background: #ffffff59;
+        width: 40px;
+        height: 40px;
+        margin: 0 15px;
+        opacity: 1;
+        display: none;
+
+        &.next-button, &.next-button-ctgr{
+            right: 0;
+        }
+
+        &.prev-button, &.prev-button-ctgr{
+            left: 0;
