+.footer{
+    background: #231F20;
+    position: relative;
+    min-height: 400px;
+    padding: 80px 0 40px;
+
+    .container-fluid{
+        position: relative;
+        z-index: 10;
+    }
+    
+    &::after{
+        content: "";
+        background-image: url('/assets/images/line-footer.png');
+        position: absolute;
+        bottom: 0;
+        left: 0;
+        width: 100%;
+        height: 25px;
+    }
+
+    &::before{
+        content: "";
+        background-image: url('/assets/images/icon-footer-background.svg');
+        width: 900px;
+        height: 330px;
+        position: absolute;
+        background-size: cover;
+        margin: auto;
+        background-repeat: no-repeat;
+        left: 0;
+        right: 0;
+        bottom: 0;
+        text-align: center;
+    }
+
+    .heading-footer{
+        text-align: center;
+        font-size: 26px;
+        text-transform: uppercase;
+        color: #fff;
+        font-weight: 300;
+    }
+
+    .form-contact{
+        display: flex;
+        align-items: center;
+        gap: 20px;
+        justify-content: center;
+        margin: 20px 0;
+
+        .form-control{
+            border-radius: 24px;
+            background: #fff;
+            min-height: 48px;
+            max-width: 240px;
+            font-size: 14px;
+        }
+
+        .btn-submit{
+            background: transparent;
+            border: 1px solid #fff;
+            color: #fff;
+            border-radius: 40px;
+            min-width: 135px;
+            min-height: 40px;
+            position: relative;
+            font-size: 14px;
+
+            &::after{
+                content: "";
+                border: 2px solid #BCBEC0;
+                padding: 21px;
+                position: absolute;
+                min-width: 143px;
+                min-height: 48px;
+                border-radius: 40px;
+                left: -5px;
+                right: 0;
+                top: -5px;
+                margin: auto;
+            }
+
+            &:hover{
+                background: #fff;
+                color: #000;
+            }
+        }
+    }
+
+    &-menu-group{
+        border-top: 1px solid #696969;
+        border-bottom: 1px solid #696969;
+        margin: 30px 0;
+        position: relative;
+
+        &::before{
+            content: "";
+            background: #272324;
+            background-image: url('/assets/images/icon-small-footer.svg');
+            width: 60px;
+            height: 20px;
+            position: absolute;
+            bottom: -9px;
+            background-repeat: no-repeat;
+            background-position: center;
+            margin: auto;
+            left: 0;
+            right: 0;
+        }
+    }
+
+    &-menu-list{
+        display: flex;
+        align-items: center;
+        list-style: none;
+        justify-content: space-evenly;
+        position: relative;
+        padding: 30px 0;
+        padding-bottom: 40px;
+
+        &--item{
+            a{
+                color: #fff;
+                text-align: center;
+                display: inline-block;
+                position: relative;
+
+                &::after{
+                    content: "";
+                    width: 100%;
+                    height: 1px;
+                    background: #fff;
+                    position: absolute;
+                    bottom: -10px;
+                    left: 0;
+                }
+            }
+        }
+    }
+
+    &-coppy-right{
+        display: flex;
+        justify-content: space-between;
+        align-items: center;
+        color: #fff;
+        margin-top: 50px;
+        margin-bottom: 50px;
+    }
+
+    @media (max-width: 767px) {
+        padding: 40px 0 10px;
+
+        &::before{
+            background-size: contain;
+            background-position: bottom;
+            width: 100%;
+            height: 100%;
+        }
+
+        .heading-footer{
+            font-size: 20px;
+        }
+
+        &-menu-list{
+            display: flex;
+            overflow-x: auto;
+            scroll-snap-type: x;
+            flex-wrap: nowrap;
+
+            &--item{
+                scroll-snap-align: start;
+                min-width: 100px;
+                text-align: center;
+            }
+        }
+    }
+}
+
+.form-with-icon{
+    position: relative;
+
+    .form-control{
+        padding-right: 45px;
+    }
+
+    .ic-form-control{
+        width: 20px;
+        position: absolute;
+        right: 10px;
+        top: 15px;
+    }
+}
+
+.cover-banner{
+    /* Thiết lập slider và các slide */
+    .slider-container {
+        position: relative;
+        overflow: hidden;
+        width: 100%;
+    }
+
+    .slider {
+        display: flex;
+        transition: transform 0.3s ease; /* Thời gian và kiểu chuyển động */
+    }
+
+    .slide {
+        flex: 0 0 100%;
+        width: 100%;
+        text-align: center;
+    }
+
+    /* Thiết lập dots */
+    .dots-container {
+        display: flex;
+        justify-content: center;
+        position: absolute;
+        bottom: 20px;
+        left: 0;
+        right: 0;
+        margin: auto;
+    }
+
+    .dot {
+        width: 5px;
+        height: 5px;
+        background-color: #fff;
+        border-radius: 40px;
+        margin: 0 5px;
+        cursor: pointer;
+    }
+
+    .dot.active {
+        background-color: #fff; /* Màu của dot hiện tại */
+        width: 60px;
+    }
+
+    @media (max-width: 767px) {
+        img {
+            width: 100%;
+            min-height: 390px;
+            -o-object-fit: cover;
+            object-fit: cover;
+            aspect-ratio: 16/9;
+            -o-object-position: left;
+            object-position: center;
+
+            &.img-mb{
+                min-height: 100vh;
+            }
+        }
+    }
+}
+
+.search-box{
