+    }
+
+    .group-production--heading .title-heading{
+        font-size: 18px;
+    }
+
+    .group-production--heading .title-heading:before{
+        min-height: 8px;
+        top: 7px;
+    }
+
+    .group-production--list{
+        display: flex;
+        flex-wrap: nowrap;
+        overflow-x: auto;
+        scroll-snap-type: x mandatory;
+        -webkit-overflow-scrolling: touch;
