+
+    .search-box .group-production--list-item{
+        scroll-snap-align: center;
+    }
+
+    .form-search-prd{
+        position: fixed;
+        top: 0;
+        width: calc(100% - 265px);
+        min-height: 73px;
+        background: #fff;
+        display: flex;
+        align-items: center;
+
+        .btn-search-form{
+            svg{
+                width: 20px;
+            }
+        }
+
+        .form-control{
+            border: none;
+            font-size: 14px;
+
+            &:focus,&:focus-visible{
+                outline: unset;
+                box-shadow: unset;
+            }
+        }
+    }
+
+    .menu-bottom{
+        display: none;
+    }
+
+    @media (max-width: 767px) {
+        padding: 20px;
+        height: calc(100vh - 73px);
+
+        .form-search-prd{
+            width: calc(100% - 80px);
+
+            .form-control{
+                border: 1px solid #d3d3d3;
+                border-radius: 40px;
+                min-height: 40px;
+            }
+
+            .btn-search-form{
+                position: absolute;
+                right: 13px;
+
+                svg{
+                    width: 18px;
+                }
+            }
+        }
+
+        .group-production--list-item{
+            width: calc(100%/2 - 8px);
+        }
+        .group-production--list{
+            flex-wrap: wrap;
+            scroll-snap-type: none;
+        }
+        .group-production--list-item{
+            min-width: unset;
+        }
+
+        &--body{
+            max-height: calc(100vh - 245px);
+            overflow: auto;
+        }
+
+        .menu-bottom{
+            display: block;
+            position: absolute;
+            bottom: 0;
+            min-height: 60px;
+            width: 100%;
+            left: 0;
+            padding: 10px 20px;
+            border-top: 1px solid #EAEAEA;
+            display: flex;
+            align-items: center;
+            justify-content: space-between;
+            background: #fff;
+    
+            a{
+                display: inline-block;
+                color: #231F20;
+                text-decoration: underline;
+            }
+        }
+    }
+}
