+
+.btn-custom{
+    display: inline-flex;
+    align-items: center;
+    justify-content: center;
+    min-width: 150px;
+    border: none;
+    border-radius: 40px;
+    height: 40px;
+    position: relative;
+    box-shadow: 0px 0px 0px 3px #BCBEC0;
+    padding: 0 40px;
+
+    &:hover{
+        color: #000;
+    }
+
+    &-black{
+        background: #231F20;
+        color: #fff;
+
+        &:hover{
+            color: #fff;
+        }
+
+        &::after{
+            content: "";
+            border: 1px solid #fff;
+            width: 96%;
+            position: absolute;
+            left: 0;
+            right: 0;
+            top: 3px;
+            height: 34px;
+            border-radius: 40px;
+            margin: auto;
+        }
+    }
+    
+    &-white{
+        background: #fff;
+        color: #000;
+
+        &::after{
+            content: "";
+            border: 1px solid #000;
+            width: 96%;
+            position: absolute;
+            left: 0;
+            right: 0;
+            top: 3px;
+            height: 34px;
+            border-radius: 40px;
+            margin: auto;
+        }
+    }
+
+    &-border{
+        &::after{
+            content: "";
+            border: 2px solid #BCBEC0;
+            padding: 21px;
+            position: absolute;
+            min-width: 158px;
+            min-height: 47px;
+            border-radius: 40px;
+            left: -5px;
+            right: 0;
+            top: -5px;
+            margin: auto;
+        }
+    }
+
+    &-hover{
+        position: relative;
+        overflow: hidden;
+    
+        &--white{
+            &::before{
+                content: "";
+                background: #fff;
+                background-image: url('/assets/images/hover-btn-white.png');
+                height: 100%;
+                width: 45px;
+                background-repeat: no-repeat;
+                position: absolute;
+                top: 0px;
+                right: -18px;
+                background-size: contain;
+                transition: .3s;
+                // right:-1000px;
+                border-radius: 100%;
+                z-index: 1;
+                opacity: 0;
+                visibility: hidden;
+            }
+        }
+    
+        &--black{
+            &::before{
+                content: "";
+                background: #000;
+                background-image: url('/assets/images/hover-btn-black.png');
+                height: 100%;
+                width: 45px;
+                background-repeat: no-repeat;
+                position: absolute;
+                top: 0px;
+                right: -18px;
+                background-size: contain;
+                transition: .3s;
+                // right:-1000px;
+                border-radius: 100%;
+                z-index: 1;
+                opacity: 0;
+                visibility: hidden;
+            }
+        }
+    
+        &:hover{
+            &::before{
+                opacity: 1;
+                visibility: visible;
+                // right: -18px;
+            }
+        }
+    }
+
+    &.btn-block{
+        &::after{
+            width: 98%;
+        }
+    }
+}
+
+/* Modal Styles */
+.modal-custom {
+    display: none;
+    position: fixed;
+    z-index: 1000;
+    left: 50%;
+    top: 50%;
+    transform: translate(-50%, -50%);
+    background-color: white;
+    padding: 20px;
+    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
+    width: 100%;
+    max-width: 620px;
+    min-height: 693px;
+
+    .modal-custom-content {
+        padding-top: 1rem;
+    }
+    
+    .close {
+        position: absolute;
+        top: 15px;
+        right: 15px;    
+        cursor: pointer;
+
+        svg{
+            width: 20px;
+        }
+    }
+
+    @media (max-width: 767px) {
+        width: 90%;
+    }
+}
+
+ /* Backdrop Styles */
+ .modal-backdrop {
+    display: none;
+    position: fixed;
+    top: 0;
+    left: 0;
+    width: 100%;
+    height: 100%;
+    background-color: rgba(0, 0, 0, 0.5);
+    z-index: 999;
+}
