+            
+            &:nth-child(1){
+                width: 100%;
+                grid-column: auto / span 2; /* Item lớn chiếm cả hai cột */
+                grid-row: auto / span 2; /* Item lớn chiếm 2 hàng */
+                height: 100%;
+                // max-height: 1350px;
+                position: relative;
+
+                .info-product{
+                    visibility: hidden;
+                    opacity: 0;
+                    position: absolute;
+                    width: 100%;
+                    max-width: 320px;
+                    bottom: 30%;
+                    z-index: 1;
+                    left: 60px;
+                    background: #ffffffd9;
+                    padding: 20px;
+                    border-radius: 20px;
+                    transition: .3s all;
+
+                    .select-color-prd{
+                        position: unset;
+                        text-align: left;
+
+                        &--group{
+                            background: transparent;
+                            padding: 0;
+                            margin: 13px 0 0;
+                        }
